mapper.update:
UPDATE notice SET notice_title=#{notice_title},
notice_content=#{notice_content},notice_modidate=now() WHERE
notice_num=#{notice_num}

UPDATE board SET board_title=#{board_title},
board_content=#{board_content},board_modidate=now() WHERE
board_num=#{board_num}

mapper.select:

SELECT b.*, c.* FROM board b, city c WHERE (board_title LIKE
CONCAT('%', #{keyword}, '%') OR member_id like CONCAT('%',
#{keyword},'%') AND c.city_code=#{city_code}
